mapper.select:
SELECT s.*, st.name AS type, sg.name AS singerName, a.album_name AS albumName
FROM song s
LEFT JOIN song_type st ON s.type_id = st.type_id
LEFT JOIN singer sg ON s.singer_id = sg.singer_id
LEFT JOIN album a ON a.album_id = s.album_id


        AND s.name LIKE CONCAT('%', #{name}, '%')


        AND s.type_id = #{typeId}


        AND s.singer_id = #{singerId}


        AND s.status = #{status}


ORDER BY s.song_id DESC
